import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import Stack from '@mui/material/Stack';
import { Card, Typography } from '@mui/material';
import '../App.css'
import emailjs from '@emailjs/browser'
import { useRef } from 'react'


export default function ContactForm() {
    const form = useRef()

    function handleFormSubmit(e) {
        e.preventDefault()
        emailjs.sendForm('service_11k5hly', 'template_eb5u3vq', form.current, 'JR7xmvkLYetkaS-cH')
        .then((result) => {
            console.log(result.text)
        }, (error) => {
            console.log(error.text)
        })
    }

    return (
        <Card variant="outlined" id="contact-form" >
            <Typography variant="h5" style={{ marginBottom: "2rem" }}>Message Me!</Typography>
            <form ref={form} onSubmit={handleFormSubmit}>
                <Stack spacing={2}>
                    <TextField
                        id="outlined-helperText"
                        label="Full Name"
                        type='text'
                        name='user_name'
                    />
                    <TextField
                        id="outlined-helperText"
                        label="Your Email"
                        type='email'
                        name='user_email'
                    />
                    <TextField
                        id="outlined-helperText"
                        label="Message"
                        type='text'
                        name='message'
                        multiline
                    />
                    <Button style={{ background: 'rgb(34, 0, 128)', padding: '1rem' }} variant="contained" type='submit' value='send'>Submit</Button>
                </ Stack>
            </form>
        </Card>
    )
}
